import { Button, Card } from "@mantine/core";
import React, { useEffect } from "react";
import Home from "./home1";
import './Page.css';
import { useState } from "react";

function modal(open,onClose)
{
    if (open===false)
    return null

    return(
        <div style={{backgroundColor:"black",zIndex:10}}>
            <Card style={{hieght:"50%",width:"50%",padding:'10px' }} withBorder>
                <h1>Modal Popup</h1>
                <Button onClick={()=>onClose()}>Close Modal</Button>
            </Card>
        </div>
    )
}

function pending()
{   
    return(
        <div>
            <Button className="corner_button" onClick={()=>{alert("You clicked add button")}}>+ ADD</Button>
                <table border={3} style={{marginTop:"70px",maxwidth:"1000px"}}>
                    <tr>
                        <td>Hello </td>
                    </tr>
                </table>
        </div>
    )
}

function bought()
{
    return(
        <div>
            <h1>This is bought page</h1>
        </div>
    )
}

function Cart()
{ 
    const [open,isOpen]=useState(false)
    const [rel,setRel]=useState(pending())

    function onClose()
    {isOpen(true)}

     useEffect(()=>{
     },[rel])
    return(
        <div>
            <Button className="Cart_Button" onClick={()=>{setRel(bought())}}>Bought</Button>
            <Button className="Cart_Button" onClick={()=>{setRel(pending())}}>Pending</Button>
            <Card className="Cart_card">
            {modal(open,onClose)}
            </Card>
        </div>
    )
}

export default Cart
