import { Button, NumberInput, Select, TextInput, Textarea } from "@mantine/core"
import axios from 'axios'
import { useState } from "react"

function Content()
{
    const date = new Date()
    const[name,setName]=useState('')
    const[quantity,setQuantity]=useState(1)
    const[type,setType]=useState('Numbers')

    return(
        <div>
        <table className="Table" cellPadding={30}>
            <tbody>
                <tr>
                    <td colSpan={3}><center><h1>Add Item</h1></center></td>
                </tr>
                <tr>
                    <td>
                    <TextInput placeholder="Name" label='Name' id="Name" onChange={(e)=>{setName(e)}} withAsterisk />
                    </td>
                    <td>
                        <NumberInput  min={0.25} defaultValue={1} step={0.05} precision={2} onChange={(e)=>{setQuantity(e)}} label="Quantity"/>
                    </td>
                    <td>
                        <Select label='Type' defaultValue={'Numbers'} searchable data={['Numbers','Kilograms','Grams','Litre','Dozen']} onChange={(e)=>{setType(e)}}/>
                    </td>
                </tr>
                <tr>
                    <td colSpan={3}>
                    <center><Button id='add_button' onClick={()=>{
                        console.log(name)
                    }}>ADD</Button></center>
                    </td>
                </tr>
            </tbody>
        </table>
        
        </div>
    )
}

export default Content;
