import { MonthPickerInput } from "@mantine/dates";
import axios from "axios";
import { useEffect, useState } from "react";

function Bought()
{
    const k=new Date()
    const [list,setList]=useState([])
    const[date,setDate]=useState(new Date())
    useEffect(()=>{
        axios.post('http://localhost:9000/allpending',{email:User.user,year:date.getFullYear(),month:date.getMonth(),bought:true})
    .then((res)=>{
        setList(res.data)})
    .catch((e)=>{console.log('Error',e)})
    },[date])
    return(
        <div>
            <center style={{width:"fit-content"}}>
            <MonthPickerInput minDate="2000-01-01" maxDate={k} label="Select month and year" onChange={setDate}/>
            </center>
            <h1>{date.toDateString()}</h1>
        </div>
    )
}
export default Bought
