import { Button, Card } from "@mantine/core";
import React, { useEffect } from "react";
import './Page.css';
import { useState } from "react";
import Modal_Control from './Modal_Control'
import { MonthPickerInput } from "@mantine/dates";
import { TextInput,NumberInput,Select } from "@mantine/core";

function Bought()
{
    return(
        <div>
            <center style={{width:"fit-content"}}><MonthPickerInput minDate="2000-01-01" maxDate="2023-08-01" label="Select month and year" /></center>
            <h1>This is bought page</h1>
        </div>
    )
}

function Cart()
{  
    function Content()
    {
    const date = new Date()
    const[name,setName]=useState('')
    const[quantity,setQuantity]=useState(1)
    const[type,setType]=useState('Numbers')

    useEffect(()=>{
        console.log(name,quantity,type)
    },[name,type,quantity])

    return(
        <div>
        <table className="Table" cellPadding={30}>
            <tbody>
                <tr>
                    <td colSpan={3}><center><h1>Add Item</h1></center></td>
                </tr>
                <tr>
                    <td>
                    <TextInput placeholder="Name" label='Name' id="Name" onChange={(e)=>{setName(e.target.value)}} withAsterisk />
                    </td>
                    <td>
                        <NumberInput  min={0.25} defaultValue={1} step={0.05} precision={2} onChange={(e)=>{setQuantity(e)}} label="Quantity"/>
                    </td>
                    <td>
                        <Select label='Type' defaultValue={'Numbers'} searchable data={['Numbers','Kilograms','Grams','Litre','Dozen']} onChange={(e)=>{setType(e)}}/>
                    </td>
                </tr>
                <tr>
                    <td colSpan={3}>
                    <center><Button id='add_button' onClick={()=>{
                        console.log(name)
                    }}>ADD</Button></center>
                    </td>
                </tr>
            </tbody>
        </table>
        
        </div>
    )
}

   function Modal()
   {
    return(<h1>Hello World</h1>)
   }

    const [rel,setRel]=useState(<Modal_Control modal={Content()} content={Modal()}/>)

    useEffect(()=>{} )
    
     return(
        <div >
            <Button className="Cart_Button" onClick={()=>{setRel(Bought())}}>Bought</Button>
            <Button className="Cart_Button" onClick={()=>{setRel(<Modal_Control modal={Content()} content={Modal()}/>)}}>Pending</Button>
            <Card className="Cart_card">
            {rel}
            </Card>
        </div>
    )
}

export default Cart
