import { Button } from "@mantine/core";
import React from "react";
import ReactDOM from "react";

function Modal({children,open,isClose}){
    
    if(!open) return null;
    
    return ReactDOM.createPortal(
        <div style={{backgroundColor:"gray",height:"400px",width:"1050px",marginTop:'100px',zIndex:4}}>
            <div style={{backgroundColor:'white', marginLeft:'400px',marginTop:'100px',padding:'50px',position:"absolute"}}>
            <h1>{children}</h1>
            <center><Button onClick={()=>{isClose()}}>Close Modal</Button></center>
            </div>
        </div>,
        document.getElementById('portal')
    )
}

export default Modal;
