import { Avatar, Button, Card, Select, TextInput } from "@mantine/core";
import { DatePicker,MonthPickerInput } from "@mantine/dates";
import React, { useEffect, useState } from "react";
import Modal_Control from "./Modal_Control";
import axios from 'axios'
import {useSelector} from 'react-redux'
import icon from './delete.ico'
import icon2 from './check.ico'
import Grid from "./list";

function Tasks()
{
    const [date,setDate]=useState(new Date())
    const [name,setName]=useState('')
    const User=useSelector(state=>state.login)
    const [pending,setpending]=useState([])
    const [completed,setCompleted]=useState([])
    useEffect(()=>{
        var pend=[]
        var done=[]
        axios.post('http://localhost:9000/alltask',{email:User.user,date,done:false})
        .then((res)=>{
            res.body.forEach(element => {
                pend.push(<Card>{element.name}
                <img src={icon} style={{position:'absolute',top:'10px',right:'5px',height:'10px',width:'10px'}}/>
                <img src={icon2} style={{position:'absolute',bottom:'10px',right:'5px',height:'10px',width:'10px'}}/>
                </Card>)
            });
            setpending(pend)
        })
        .catch((e)=>{console.log(e)})

        axios.post('http://localhost:9000/alltask',{email:User.user,date,done:true})
        .then((res)=>{
            res.body.forEach(element=>{
                done.push(<Card>
                    {element.name}
                    <img src={icon} style={{position:'absolute',top:'10px',right:'5px',height:'10px',width:'10px'}}/>
                </Card>)
            })
            setCompleted(done)
        })
        .catch((e)=>{console.log(e)})
        
    })

    function modal()
    {
        return(
            <center>
            <div>
            <table className="Table" cellPadding={30}>
            <tbody>
                <tr>
                    <td colSpan={3}><center><h1>Add a Task</h1></center></td>
                </tr>
                <tr>
                    <td>
                    <TextInput placeholder="Name" label='Name of Task' id="Name" withAsterisk onChange={setName} />
                    </td>
                </tr>
                <tr>
                    <td colSpan={3}>
                    <center>
                      <Button id='add_button' onClick={()=>{axios.post('http://localhost:9000/task',{email:User.user,name,date})
                      .then((res)=>{console.log(res)})
                        .catch((e)=>{console.log(e)})
                    }}>ADD</Button></center>
                    </td>
                </tr>
            </tbody>
        </table>
            </div>
            </center>
        )
    }

    function content()
    {
        return(
            <div>
            <h1>Tasks</h1>
            <Card>
                <Grid>{pending}</Grid>
            </Card>
            <Card>
                <Grid>{completed}</Grid>
            </Card>
            </div>
        )
    }
    return(
        <div id="Tasks_Back">
        <center>
        <h1>TASKS</h1>
        <div >
            <Card id='calendar' withBorder>
        <DatePicker renderDay={(date)=>{const day=date.getDate();
        return((day==1)?(
        <Avatar color='yellow' variant="filled" radius={20}>
        <div style={{color:'green' , fontSize:20}}>{day}<sup style={{color:'red',top:0,right:2,position:'absolute',fontSize:20}}><img style={{width:"1px",height:"1px"}} src="./check.png"/></sup></div></Avatar>) : (<div>{day}</div>)
    )}} 
        defaultValue={new Date()} onChange={setDate} hideOutsideDates/>
        </Card>
        </div>
        </center>
        <div id='Event_Display'> 
        <Modal_Control modal={modal()} content={content()} /> 
        </div>
        </div>
    )
}

export default Tasks;
