import React, { useEffect, useState } from "react";
import {DatePicker} from '@mantine/dates'
import './Page.css'
import { Avatar, Card } from "@mantine/core";
function Event()
{
    const [date,setDate]=useState(new Date())
    useEffect(()=>{
        console.log(date)
    },[])

    return(
        <div>
        <center>
        <h1>EVENTS</h1>
        <div>
            <Card id='calendar' withBorder>
        <DatePicker renderDay={(date)=>{const day=date.getDate();
        return((day==1)?(
        <Avatar color='yellow' variant="filled" radius='md'>
        <div style={{color:'green' , fontSize:20}}>{day}<sup style={{color:'red',top:0,right:2,position:'absolute',fontSize:20}}>.</sup></div></Avatar>) : (<div>{day}</div>)
    )}} 
        defaultValue={new Date()} onChange={setDate} allowDeselect hideOutsideDates/>
        </Card>
        </div>
        </center>
        <div id='Event_Display'> <h1>{date.toLocaleDateString()}</h1></div>
        </div>
    )
}

export default Event;
