import { Button, Card } from "@mantine/core";
import React, { useEffect } from "react";
import Home from "./home1";
import './Page.css';
import { useState } from "react";
import Modal from "./Modal";


function Pending(open,Open,Close)
{   
    return(
        <div>
            <Button className="corner_button" onClick={()=>{Open()}}>+ ADD</Button>
                <table border={3} style={{marginTop:"70px",maxwidth:"1000px"}}>
                    <tr>
                        <td><Modal open={open}  isClose={Close} >Hello World</Modal> </td>
                    </tr>

                    <tr>
                        <td> Hello </td>
                    </tr>
                    
                </table>
        </div>
    )
}

function Bought()
{
    return(
        <div>
            <h1>This is bought page</h1>
        </div>
    )
}

function Cart()
{  
    const[open,setOpen]=useState(false)
    function Open(){setOpen(true)}
    function Close(){setOpen(false)}
    const [rel,setRel]=useState(Pending(open,Open,Close))
     useEffect(()=>{console.log(open)
     },[rel,open])
    return(
        <div>
            <Button className="Cart_Button" onClick={()=>{setRel(Bought())}}>Bought</Button>
            <Button className="Cart_Button" onClick={()=>{setRel(Pending(open,Open,Close))}}>Pending</Button>
            <Card className="Cart_card">
            {rel}
            </Card>
        </div>
    )
}

export default Cart
