import React from "react";
import { Button } from "@mantine/core";
import { Textarea,NumberInput } from "@mantine/core";
import ReactDOM  from "react-dom";
import './Page.css';

function Modal({ children, open, isClose }) {
  if (!open) return null;

  return (
    <div className="Modal_Back">
      <div className="Modal_Inner">
        <a id='close'onClick={()=>{isClose()}}>x</a>
        <table className="Table" cellPadding={30}>
            <tbody>
                <tr>
                    <td><center><h1>Add Items</h1></center></td>
                </tr>
                <tr>
                    <td>
                    <Textarea placeholder="Name" label='Name of Item' id="Name" size="10px"/>
                    </td>
                    <td>
                        <NumberInput  label="Quantity"/>
                    </td>
                </tr>
                <tr>
                    <td>
                    <center><Button>ADD</Button></center>
                    </td>
                </tr>
            </tbody>
        </table>
        
        
        
      </div>
    </div> 
)
  }
export default Modal;
