{
    const [date,setDate]=useState(new Date())
    let options={date:'numeric',month:'long',day:'numeric'}
    useEffect(()=>{
        console.log(date)
    },[])

    return(
        <div>
        <center>
        <h1>EVENTS</h1>
        <div>
            <Card id='calendar' withBorder>
        <DatePicker allowDeselect defaultValue={new Date()} onChange={setDate} />
        </Card>
        </div>
        </center>
        <div id='Event_Display'> <h1>{date.toLocaleDateString}</h1></div>
        </div>
    )
}

export default Event;
